mapper.update:
INSERT INTO ( CAR_SEQ ,SEQ ,COMP_NAME ,LOCATION ,START_PERIOD ,END_PERIOD ,TASK ,SALARY ) SELECT CAST(CAREER_SEQ.NEXTVAL AS VARCHAR2(255)), S.* FROM(
) S MERGE INTO CAREER car USING (
SELECT #{item.recSeq, jdbcType=VARCHAR} ,#{item.carCompany, jdbcType=VARCHAR} ,#{item.carLoc, jdbcType=VARCHAR} ,#{item.carStart, jdbcType=VARCHAR} ,#{item.carEnd, jdbcType=VARCHAR} ,#{item.carTask, jdbcType=VARCHAR} ,#{item.carSalary, jdbcType=VARCHAR} FROM DUAL
) du ON (edu.EDU_SEQ = du.eduSeq AND edu.SEQ = du.recSeq)
WHEN MATCHED THEN UPDATE SET edu.SCHOOL_NAME = du.eduSchool ,edu.DIVISION = du.eduDiv ,edu.START_PERIOD = du.eduStart ,edu.END_PERIOD = du.eduEnd ,edu.MAJOR = du.eduMajor ,edu.GRADE = du.eduGrade ,edu.LOCATION = du.eduLoc
WHEN NOT MATCHED THEN INSERT ( EDU_SEQ ,SEQ ,SCHOOL_NAME ,DIVISION ,START_PERIOD ,END_PERIOD ,MAJOR ,GRADE ,LOCATION ) VALUES( du.eduSeq ,du.recSeq ,du.eduSchool ,du.eduDiv ,du.eduStart ,du.eduEnd ,du.eduMajor ,du.eduGrade ,du.eduLoc )

mapper.insert:
    	INSERT INTO 
    		CERTIFICATE(
    			CERT_SEQ
				,SEQ
				,QUALIFI_NAME
				,ACQU_DATE
				,ORGANIZE_NAME
    		)
   		SELECT CAST(CERT_SEQ.NEXTVAL AS VARCHAR2(255)), S.*
   		FROM (

    		SELECT 
    			#{item.recSeq, jdbcType=VARCHAR}
    			,#{item.certQualifi, jdbcType=VARCHAR}
    			,#{item.certAcqu, jdbcType=VARCHAR}
    			,#{item.certOrganize, jdbcType=VARCHAR}
    		FROM DUAL

   		) S

mapper.update:
   	 	UPDATE 
   	 		RECRUIT 
   	 	SET
			NAME = #{recName, jdbcType=VARCHAR}
			,BIRTH = #{recBirth, jdbcType=VARCHAR}
			,GENDER = #{recGender, jdbcType=VARCHAR}
			,PHONE = #{recPhone, jdbcType=VARCHAR}
			,EMAIL = #{recEmail, jdbcType=VARCHAR}
			,ADDR = #{recAddr, jdbcType=VARCHAR}
			,LOCATION = #{recLoc, jdbcType=VARCHAR}
			,WORK_TYPE = #{recWt, jdbcType=VARCHAR}
			,SUBMIT = #{recSubmit, jdbcType=VARCHAR}
	    WHERE
	     	SEQ = #{recSeq, jdbcType=VARCHAR}

update.foreach:

    	UPDATE 
   	 		CAREER 
   	 	SET
			COMP_NAME = #{item.carCompany, jdbcType=VARCHAR}
			,LOCATION = #{item.carLoc, jdbcType=VARCHAR}
			,START_PERIOD = #{item.carStart, jdbcType=VARCHAR}
			,END_PERIOD = #{item.carEnd, jdbcType=VARCHAR}
			,TASK = #{item.carTask, jdbcType=VARCHAR}
			,SALARY = #{item.carSalary, jdbcType=VARCHAR}
	    WHERE
	     	CAR_SEQ = #{item.carSeq, jdbcType=VARCHAR} AND SEQ = #{item.recSeq, jdbcType=VARCHAR}

    	UPDATE 
   	 		CERTIFICATE 
   	 	SET
			QUALIFI_NAME = #{item.certQualifi, jdbcType=VARCHAR}
			,ACQU_DATE = #{item.certAcqu, jdbcType=VARCHAR}
			,ORGANIZE_NAME = #{item.certOrganize, jdbcType=VARCHAR}
	    WHERE
	     	CERT_SEQ = #{item.certSeq, jdbcType=VARCHAR} AND SEQ = #{item.recSeq, jdbcType=VARCHAR}
